import pandas as pd
import numpy as np
import seaborn as sns
import matplotlib.pyplot as plt

sns.set()

# Create example DataFrame of stock values
data_frame = pd.DataFrame({
    'ticker':np.repeat( ['aapl','goog','yhoo','msft'], 25 ),
    'date':np.tile( pd.date_range('1/1/2011', periods=25, freq='D'), 4 ),
    'price':(np.random.randn(100).cumsum() + 10) })

data_frame['split'] = False
data_frame['split 2'] = "No"


plt.figure()
sns.scatterplot(x=data_frame.date.dt.strftime('%Y-%w'), y='price', data=df, hue='split')
plt.figure()
sns.scatterplot(x=data_frame.date.dt.strftime('%Y-%w'), y='price', data=df, hue='split 2')